import { Component, OnInit, forwardRef } from '@angular/core';
import { ControlValueAccessor, FormControl, FormGroup, NG_VALUE_ACCESSOR, Validators } from '@angular/forms';
import { CandidateService } from '../services/candidate.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-candidate',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => CandidateComponent),
      multi: true,
    }
  ],
  templateUrl: './candidate.component.html',
  styleUrls: ['./candidate.component.css']
})
export class CandidateComponent implements OnInit {
  candidateForm: FormGroup | undefined;

  constructor(private candidateService: CandidateService, private router: Router) { }

  ngOnInit(): void {
      this.candidateForm = new FormGroup({
      applyingPosition: new FormControl('', [Validators.required]),
      candidateFirstName: new FormControl('', [Validators.required]),
      candidateLastName: new FormControl('', [Validators.required]),
      candidateEmail: new FormControl('', [Validators.required, Validators.email]),
      candidatePhoneNo: new FormControl('', [Validators.required]),
      candidateSkills: new FormControl('', [Validators.required]),
      joiningAvailability: new FormControl('', [Validators.required]),
      candidateResume: new FormControl(),
      comments: new FormControl()
    })
  }

  submitDetails() {

    const formData=new FormData();
    formData.append("candidate", this.candidateForm?.value);
    formData.append("candidateResume", this.candidateForm?.val);

    this.candidateService.saveCandidateData(this.candidateForm?.value).subscribe(res => {
      this.router.navigate(['/setInterview'])
      // this.candidateForm.reset();
    })
  }

  onFileChange(event:any){
    console.log(event);
  }

}
