import { Component, OnInit, forwardRef } from '@angular/core';
import { ControlValueAccessor, FormControl, FormGroup, NG_VALUE_ACCESSOR } from '@angular/forms';

@Component({
  selector: 'app-candidate',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => CandidateComponent),
      multi: true,
    }
  ],
  templateUrl: './candidate.component.html',
  styleUrls: ['./candidate.component.css']
})
export class CandidateComponent implements ControlValueAccessor {
  constructor(){}

  disabled=false;
  writeValue(applyingPosition: any): void {
    this.candidateForm.controls.applyingPosition=applyingPosition
  }
  registerOnChange(fn: any): void {
    console.log(fn);    
  }
  registerOnTouched(fn: any): void {
    console.log(fn);    
  }
  setDisabledState?(isDisabled: boolean): void {
    this.disabled=isDisabled    
  }

  candidateForm=new FormGroup({
    applyingPosition:new FormControl(),
    candidateFirstName:new FormControl(),
    candidateLastName:new FormControl(),
    candidateEmail:new FormControl(),
    candidatePhoneNo:new FormControl(),
    candidateSkills:new FormControl(),
    joiningAvailability:new FormControl(),
    comments:new FormControl()
  })

  submitDetails(){
    console.log(this.candidateForm.value);
  }

}
