import { Component, OnInit, forwardRef } from '@angular/core';
import { FormControl, FormGroup, NG_VALUE_ACCESSOR, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CandidateService } from '../services/candidate.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { CandidateResponse } from '../CandidateResponse';

@Component({
  selector: 'app-candidate',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => CandidateComponent),
      multi: true,
    }
  ],
  templateUrl: './candidate.component.html',
  styleUrls: ['./candidate.component.css']
})
export class CandidateComponent implements OnInit {

  candidateForm:any = FormGroup;
  fileName = '';

  file!:Blob;
  candidate!: CandidateResponse;

  constructor(private candidateService: CandidateService, private router: Router, private _snackBar: MatSnackBar) { }

  ngOnInit(): void {
    this.candidateForm = new FormGroup({
      applyingPosition: new FormControl('', [Validators.required]),
      candidateFirstName: new FormControl('', [Validators.required]),
      candidateLastName: new FormControl('', [Validators.required]),
      candidateEmail: new FormControl('', [Validators.required, Validators.email]),
      candidatePhoneNo: new FormControl('', [Validators.required]),
      candidateSkills: new FormControl('', [Validators.required]),
      joiningAvailability: new FormControl('', [Validators.required]),
      comments: new FormControl(),
      file: new FormControl()
    })
  }

  submitDetails() {



    const formValue = this.candidateForm;
    if(formValue && this.candidate){
      this.candidate.candidateFirstName = formValue.candidateFirstName;
      this.candidate.applyingPosition = formValue.applyingPosition;
      this.candidate.candidateLastName = formValue.candidateLastName;
      this.candidate.candidateEmail = formValue.candidateEmail;
      this.candidate.candidatePhoneNo = formValue.candidatePhoneNo;
      this.candidate.candidateSkills = formValue.candidateSkills;
      this.candidate.joiningAvailability = formValue.joiningAvailability;
      this.candidate.file = formValue.file;
      this.candidate.comments = formValue.comments;
    }

    let formData = new FormData();
    formData.append('candidate', JSON.stringify(this.candidate))
    formData.append('file',this.file)
    this.candidateService.saveCandidateData(formData).subscribe(res => {

       res.next(""); res.complete(); 
    })
  }


  onFileChange(event: any) {
    // this.formData.append("file", event.target.files[0]);
    this.file = event.target.files[0];

    // this.formData.append('id', history.state.jsonData.id);

    // this.service.upload(this.formData).subscribe(data => {
    //   console.log(data.response)
    //   if (data.response) {
