import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-candidate',
  templateUrl: './candidate.component.html',
  styleUrls: ['./candidate.component.css']
})
export class CandidateComponent {
  constructor(){}

  candidateForm=new FormGroup({
    applyingPosition:new FormControl(),
    candidateFirstName:new FormControl(),
    candidateLastName:new FormControl(),
    candidateEmail:new FormControl(),
    candidatePhoneNo:new FormControl(),
    candidateSkills:new FormControl(),
    joiningAvailability:new FormControl(),
    comments:new FormControl()
  })

  submitDetails(){
    console.log(this.candidateForm.value);
  }

}
