import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { candidate } from '../candidate';

@Injectable({
  providedIn: 'root'
})
export class CandidateService {

  candidateupdateData:any;  

  constructor(private http:HttpClient) {}

  saveCandidateData(body:any){

    let httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'multipart/form-data' // Set the desired content type here
      })
    };
    return this.http.post("http://localhost:8080/api/v1/candidate/save/candidate", body, httpOptions)
  }

  getAllCandidateData(){
    return this.http.get("http://localhost:8080/api/v1/candidate/getAll/candidate")
  }

  editCandidateData(id:any, body:any){   
    return this.http.put("http://localhost:8080/api/v1/candidate/editCandidate/"+id, body)
  }
}
