import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { candidate } from '../candidate';

@Injectable({
  providedIn: 'root'
})
export class CandidateService {

  candidateupdateData:any;  

  constructor(private http:HttpClient) {}

  saveCandidateData(body:any, file:File){
    const formData=new FormData();
    formData.append("candidate", body);
    formData.append("candidateResume", file);
    return this.http.post("http://localhost:8080/api/v1/candidate/save/candidate", formData)
  }

  getAllCandidateData(){
    return this.http.get("http://localhost:8080/api/v1/candidate/getAll/candidate")
  }

  editCandidateData(id:any, body:any){   
    return this.http.put("http://localhost:8080/api/v1/candidate/editCandidate/"+id, body)
  }
}
