import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { CandidateResponse } from '../CandidateResponse';

@Injectable({
  providedIn: 'root'
})
export class CandidateService {

  candidateupdateData:any;  

  constructor(private http:HttpClient) {}

  saveCandidateData(body:any):Observable<CandidateResponse>{
    // console.log(JSON.stringify(body))
    new Observable<CandidateResponse>((observer)=>
    return this.http.post("http://localhost:8080/api/v1/candidate/save/candidate", body)
  //  return this.http.post<ResponseBean<any>>(url, formData, httpOptions);

  }

  getAllCandidateData(){
    return this.http.get("http://localhost:8080/api/v1/candidate/getAll/candidate")
  }

  editCandidateData(id:any, body:any){   
    return this.http.put("http://localhost:8080/api/v1/candidate/editCandidate/"+id, body)
  }
}
